import { useEffect, useState } from 'react';

const dummy = [
  {
    boardName: '자유게시판',
    boardLink: 'free',
    postList: [
      {
        postTitle: '게시물1',
      },
      { postTitle: '게시물2' },
    ],
  },
  {
    boardName: '유자게시판',
    boardLink: 'yuzu',
    postList: [
      {
        postTitle: '게시물1',
      },
      { postTitle: '게시물2' },
    ],
  },
];

function Main() {
  const [msg, setMsg] = useState<any>();
  useEffect(() => {
    fetch('http://127.0.0.1:3000/', { method: 'GET' })
      .then((response) => {
        response.json();
      })
      .then((data) => {
        console.log(data);
        setMsg(data);
      });
  }, []);

  return (
    <div>
      메인 페이지 입니다!
      {dummy.map((element, index) => {
        return (
          <div key={index}>
            <div>{element.boardName}</div>
            <div>{element.boardLink}</div>
            <div>
              {element.postList.map((ele, idx) => {
                return <div key={idx}>{ele.postTitle}</div>;
              })}
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Main;
